CREATE TABLE user(
id int(11) NOT NULL AUTO_INCREMENT,
name varchar(32),
number varchar(32),
email varchar(32),
password varchar(255),
dob date DEFAULT NULL,
gender int,
PRIMARY KEY (id)
);

CREATE TABLE inventory(
id int(11) NOT NULL AUTO_INCREMENT,
